import React from "react";
import "./Cards.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGithub, faLinkedin } from '@fortawesome/free-brands-svg-icons';

function Cards({ profileImage }) {
  return (
    <div className="containercard">
      <div className="container">
        <div className="card">
          <div className="front">
            <div className="card-top">
              <p className="card-top-para">Profile</p>
            </div>

            <img src={profileImage} alt="Profile" className="profile-image" />

            <p className="heading">Front Card</p>
            <p className="follow">Follow me for more...</p>
          </div>
          <div className="back">
            <p className="heading">Follow Me</p>

            <div className="icons">
              {/* Ajoutez vos icônes de médias sociaux */}
              <a href="lien_vers_votre_profil_github"><FontAwesomeIcon icon={faGithub} /></a>
              <a href="lien_vers_votre_profil_linkedin"><FontAwesomeIcon icon={faLinkedin} /></a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Cards;
