import React, { useState } from 'react';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import Checkbox from '@mui/material/Checkbox';
import FormControlLabel from '@mui/material/FormControlLabel';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/scrollbar';
import './Background.css';
import 'swiper/css/autoplay';
import image1 from '../../assets/img1.png';
import image2 from '../../assets/img2.png';
import image3 from '../../assets/img3.png';
import image4 from '../../assets/img4.png';
import image5 from '../../assets/img5.jpeg';
import Swal from 'sweetalert2';

const images = [image1, image2, image3, image4, image5];

const Background = () => {
  const [open, setOpen] = useState(false);
  const [checked, setChecked] = useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleCheckboxChange = (event) => {
    setChecked(event.target.checked);
  };

  const handleModalButtonClick = () => {
    handleClose();
    handleClick();
  };

  const handleClick = () => {
    Swal.fire({
      background:"#0e0e1a",
      color:"white",
      title: "Are you sure?",
      text: "You want to predict this!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#da7dac",
      cancelButtonColor: "#b78de2",
      confirmButtonText: "Yes"
    }).then((result) => {
      if (result.isConfirmed) {
        Swal.fire({
          background:"#0e0e1a",
          color:"white",
          title: "Okay!",
          text: "Progress...",
          icon: "success",
        });
      }
    });
  };

  const LeftSection = () => (
    <div className="left-section">
      <div>
        <h3>State of the Art Technology</h3>
        <p>Our image prediction service utilizes cutting-edge machine learning algorithms to analyze and predict the content of images with unparalleled accuracy.
        Powered by deep learning models, our system can identify objects, scenes, and even complex patterns in images, providing valuable insights and enhancing your understanding of visual data.
        </p>
      </div>
      <a href="#" className="btn" onClick={handleClickOpen}><span>Start Predict</span></a>
    </div>
  );

  const RightSection = () => (
    <div className="right-section">
      <Swiper
        modules={[Autoplay]}
        slidesPerView={1}
        autoplay={{ delay: 1000 }}
      >
        {images.map((image, index) => (
          <SwiperSlide key={index}>
            <img className="img slide-img" src={image} alt="" />
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );

  return (
    <div className="containerBack">
      <LeftSection />
      <RightSection />
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Modal with Checkbox</DialogTitle>
        <DialogContent>
          <FormControlLabel
            control={
              <Checkbox
                checked={checked}
                onChange={handleCheckboxChange}
                color="primary"
              />
            }
            label="Check me"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Close</Button>
          <Button onClick={handleModalButtonClick}>Confirm</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default Background;
